package tp3;

public class Tp3Exo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n = 10, p = 4;   
		long q = 2111000222 ;   
		float x = 1.75f;
		System.out.println("x + n = " + (n+x));
		System.out.println("n % p * q = " +  (n % p * q));
		System.out.println(" n % -p * n  = " + ( n % -p * n ));
		System.out.println(" -n % p * n = " + ( -n % p * n));
		
	}

}
